DROP PROCEDURE IF EXISTS `sp_saleswithcost`;
DELIMITER $$

CREATE DEFINER=`dbb`@`192.168.1.94` PROCEDURE `sp_saleswithcost`(IN `date_start` DATE, IN `date_end` DATE, IN `filter` VARCHAR(50))
BEGIN

DROP TEMPORARY TABLE IF EXISTS TEMP;
CREATE TEMPORARY TABLE TEMP (
	TransDate Date,
	TransID VARCHAR(30),
	Qty int,
	Unit VARCHAR(10),
	Code int,
	ItemName VARCHAR(100),
	Discount double(10,2),
	SRP double(18,2),
	TotalSRP double(18,2),
	NetofVat double(18,2),
	Cost double(18,2),
	TotalCost double(18,2),
	PayMode varchar(50),
	Brand varchar(50),
	Supplier varchar(50),
	Category varchar(50),
	sortkey int
);

set @strsql = 'INSERT INTO TEMP select b.dateSO, 
(case 
when b.jeid <> "" then concat("(JE) ",b.jeid)
when b.salesInvc <> "0" and b.soid = "0" then concat("(INV) ",b.salesInvc)
else
concat("(SO) ",b.soID)
end) as TransID,
a.qty, e.Unit, a.idItem, c.itemname, a.discount, 
(case b.type
when "Marketing Expense" then 0
when "Operating Expense" then 0
else
a.unitprice
end) as SRP, 
(case b.type
when "Marketing Expense" then 0
when "Operating Expense" then 0
else
(a.qty*a.unitprice)-((a.qty*a.unitprice)*(a.discount/100))
end) as totalSRP,
(case b.type
when "Marketing Expense" then 0
when "Operating Expense" then 0
else
(((a.qty*a.unitprice)-((a.qty*a.unitprice)*(a.discount/100)))/1.12)
end) as NetofVat,
h.cost, (a.qty*h.cost) as totalCost ,  
concat(b.payMode," - ",b.type) as paymode, UPPER(f.brandname) as brand , UPPER(g.suppliername) as supplier , d.Category, 0
from tblsales a
left join tblsalesorder b on a.id=b.id
left join tblitem c on a.idItem=c.code
left join tblitemcategory d on d.idCategory = c.idcategory
left join tblunit e on e.idUnit=c.idUnit
left join tblitembrand f on f.idbrand = c.idbrand
left join tblsupplier g on g.idsupplier = c.idsupplier
left join tblinventory h on h.code = a.iditem';
set @date_start = date_start;
set @date_end = date_end;
set @filter = concat('%',filter,'%');
	if filter = '' or filter = 'All' then
		set @strsql = concat(@strsql," ","where (b.dateSO between ? and ?)");
		PREPARE STMT FROM @strsql;
		EXECUTE STMT using @date_start, @date_end;
	else
		set @strsql = concat(@strsql," ","where (b.dateSO between ? and ?) and d.Category like ?");
		PREPARE STMT FROM @strsql;
		EXECUTE STMT using @date_start, @date_end, @filter;
	end if;

DEALLOCATE PREPARE STMT;
UPDATE TEMP SET sortkey = 1 where Category like '%consign%';
select * from TEMP order by sortkey, Category, TransDate;

END $$

